import './App.css';
import Slider from "react-slick";
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";

import {foodItem} from "./Data"
function App() {
  const settings = {
    dots: true,
    infinite: false,
    speed: 500,
    slidesToShow: 4,
    slidesToScroll: 4,
    initialSlide: 0,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 3,
          infinite: true,
          dots: true
        }
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 2,
          initialSlide: 2
        }
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1
        }
      }
    ]
  };
  return (
    <div className="App">
      <Slider {...settings}>
      {foodItem.map((item) => (
         <div className="card">
         <div className="cadr-top">
          <img src={item.title} alt={item.title} />
          <p>{item.cover}</p>
         </div>
         <div className='card-bottom'>
          <h3>{item.price}</h3>
         </div>
        </div>

      ))}
      </Slider>



    </div>
  );
}

export default App;
